StructuredTextModel.Text:
(* Mise à l'échelle pour obtenir des degrés Celsius *)
Température:= (101.0*(UINT_TO_REAL(EntréeAnalogique)/1000.0))-23.0;

(* Base de temps au quart d'heure *)
IF Get1minClk() AND NOT Front1 THEN
	Front1:= TRUE;
	IF Minute>=15 THEN
		Minute:= 1;
	ELSE
		Minute:= Minute+1;
	END_IF;
END_IF;

IF NOT Get1minClk() THEN
	Front1:= FALSE;
END_IF;
		
(* Range la température en tête de pile, tous les quarts d'heure sur 24 heures (96 x quarts d'heure) *)
IF Minute=1 AND NOT Front2 THEN
	Front2:= TRUE;
	(* Pousse la pile et libère l'en-tête (emplacement 0) *)
	FOR index1:= 94 TO 0 BY -1 DO
		Pile[index1+1]:= Pile[index1];
	END_FOR;
	(* Range la température *)
	Pile[0]:=Température;
	
	(* Calcule la tendance *)
	IF Pile[0]>Pile[1] THEN
		Tendance:= 1;
	ELSIF  Pile[0]<Pile[1] THEN
		Tendance:= -1;
	ELSE
		Tendance:= 0;
	END_IF;
	
	(* Parcourt la pile pour rechercher les températures maximale et minimale et la moyenne sur les dernières 24 heures *)
	TempératureMIN:= Pile[0];
	TempératureMAX:= Pile[0];
	Moyenne:= 0.0;
	FOR index2 := 0 TO 95 DO
		Moyenne:= Moyenne+Pile[index2];
		IF Pile[index2]<TempératureMIN THEN
			TempératureMIN:= Pile[index2];
		ELSIF Pile[index2]>TempératureMAX THEN
			TempératureMAX:= Pile[index2];
		END_IF;
	END_FOR;
	Moyenne:= Moyenne/96.0;
END_IF;

IF Minute<>1 THEN
	Front2:= FALSE;
END_IF;
